export default {
  name: "Pagination",
  props: ["pageNo", "pageSize", "total", "continues"],
  computed: {
    totalPage() {
      return Math.ceil(this.total / this.pageSize);
    },

    startNumAndEndNum() {
      const {
        pageNo,
        totalPage,
        continues
      } = this;
      let start = 0;
      let end = 0;

      if (totalPage < continues) {
        start = 1;
        end = totalPage;
      } else {
        start = pageNo - parseInt(continues / 2);
        end = pageNo + parseInt(continues / 2);

        if (start < 1) {
          start = 1;
          end = continues;
        }

        if (end > totalPage) {
          start = totalPage - continues + 1;
          end = totalPage;
        }
      }

      return {
        start,
        end
      };
    }

  }
};
