import Swiper from "swiper";
export default {
  name: "ImageList",

  data() {
    return {
      currentIndex: 0
    };
  },

  props: ["skuImageList"],
  watch: {
    skuImageList(newValue, oldValue) {
      this.$nextTick(() => {
        new Swiper(this.$refs.cur, {
          // 如果需要前进后退按钮
          navigation: {
            nextEl: ".swiper-button-next",
            prevEl: ".swiper-button-prev"
          },
          slidesPerView: 3
        });
      });
    }

  },
  methods: {
    changeIndex(index) {
      this.currentIndex = index;
      this.$bus.$emit('getIndex', this.currentIndex);
    }

  }
};
