import { reqGoodsInfo } from "@/api"
const state = {}
const mutations = {
    GETGOODINFO
}
const actions = {
    async getGoodInfo({commit},skuId) {
        let result = await reqGoodsInfo(skuId)
        if (result.code == 200) {
            commit('GETGOODINFO',skuId)
        }
    }
}
const getters = {}
export default {
    state,mutations,actions,getters
}
