export default {
  name: "Register",

  data() {
    return {
      phone: "",
      code: "",
      password: "",
      password1: "",
      agree: true
    };
  },

  methods: {
    async getCode() {
      try {
        const {
          phone
        } = this;
        phone && (await this.$store.dispatch("getCode", phone));
        this.code = this.$store.state.user.code;
      } catch (error) {
        alert(error.message);
      }
    },

    async userRegister() {
      const success = await this.$validator.validateAll();

      if (success) {
        try {
          const {
            phone,
            password,
            password1,
            code
          } = this;
          phone && code && password == password1 && (await this.$store.dispatch("userRegister", {
            phone,
            code,
            password
          }));
          this.$router.push("/login");
        } catch (error) {
          alert(error.message);
        }
      }
    }

  }
};
