export default {
  name: 'Login',

  data() {
    return {
      phone: '',
      password: ''
    };
  },

  methods: {
    async userLogin() {
      try {
        const {
          phone,
          password
        } = this;
        phone && password && (await this.$store.dispatch('userLogin', {
          phone,
          password
        }));
        let toPath = this.$route.query.redirect || 'home';
        this.$router.push(toPath);
      } catch (error) {
        alert(error.message);
      }
    }

  }
};
